bindEvents=function(){$(".off").altaiOff(),$(".sortable").altaiSortable(),$(".altai-center").altaiCenter(),$("option").each(function(){t=$(this).text(),$(this).text(t.replace(/^(.)|\s(.)/g,function(t){return t.toUpperCase()}))}),$("[type=file]").on("change",function(){var t=this.files[0].name,e=$(this).attr("placeholder");""!=$(this).val()?$(this).next().text(t):$(this).next().text(e)}),$(".toggle-trigger").click(function(){$(".toggle-box").slideToggle("fast")})},$(document).on("turbolinks:load",function(){bindEvents(),Turbolinks.clearCache()});